var url = 'https://webflow.com/oauth/authorize?client_id=' + config.clientId + '&response_type=code';
var win = window.open(url, 'Authorization page', 'toolbar=no,scrollbars=no,location=no,statusbar=no,menubar=no,resizable=0,width=500,height=600,left=' + ((screen.width / 2) - 250) + ',top=' + ((screen.height / 2) - 250) + ',');
var self = this;
var intervalFn = function () {
try {
    if (!win || !win.location) {
        window.clearInterval(pollTimer);
    } else {
        if (win.location.href.indexOf('/callback') != -1) {
			win.innerWidth = 100;
            win.innerHeight = 100;
            win.screenX = screen.width;
            win.screenY = screen.height;
            window.clearInterval(pollTimer);
            url = win.location.href;
            var pos = url.indexOf('code=');
            var code;
            if(pos > -1) {
                code = url.substring(pos + 5);
                config.authorizationCode = code;
                var request = {

                };
                var res = app.endpoints.http.post({
                    path: 'https://api.webflow.com/oauth/access_token',
                    body: {
                        client_id: config.clientId,
                        client_secret: config.clientSecret,
                        code: config.authorizationCode,
                        grant_type: 'authorization_code',
                        redirect_uri: config.redirectUri
                    }
                });
                if (res) {
                    config.accessToken = res.access_token;
                    console.log('Success:', res);
                } else {
                    console.error('Error');
                }
                try { 
                    win.close();
                } catch (e) { }
            }
        }
    }
} catch (e) {
    console.log(e)
}
};
var pollTimer = window.setInterval(function () { intervalFn.apply(self) }, 500);
